 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { ItemType } from '../../domain/rapi-dev-kit_db/item-type';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../ItemType.service.ts
 */

/*
 * SCHEMA DB ItemType
 *
	{
		Name: {
			type: 'String'
		},
		Schema: {
			type: 'String'
		},
		Validation: {
			type: 'String'
		},
		Visualization: {
			type: 'String'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		ItemType: {
			type: Schema.ObjectId,
			required : true,
			ref : "BlockType"
		},
		Type: {
			type: Schema.ObjectId,
			required : true,
			ref : "Item"
		},
	}
 *
 */
@Injectable()
export class ItemTypeBaseService {

    contextUrl: string = environment.endpoint + '/types';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * ItemTypeService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: ItemType): Observable<ItemType> {
        return this.http
            .post<ItemType>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * ItemTypeService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ItemTypeService.findByName
    *   @description CRUD ACTION findByName
    *   @param Objectid key Id of the resource Name to search
    *
    */
    findByName(id: string): Observable<ItemType[]> {
        return this.http
            .get<ItemType[]>(this.contextUrl + '/findByName/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * ItemTypeService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id 
    *
    */
    get(id: string): Observable<ItemType> {
        return this.http
            .get<ItemType>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ItemTypeService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<ItemType[]> {
        return this.http
            .get<ItemType[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * ItemTypeService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: ItemType): Observable<ItemType> {
        return this.http
            .post<ItemType>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
